// optional import of sdk modules
import { execution } from '@dynatrace-sdk/automation-utils';

export default async function ({ execution_id }) {
  const configGet = await fetch(`/platform/automation/v1/executions/${execution_id}/tasks/wait-for-ssm-execution-completed/result`);
  const configBody = await configGet.json();
  const status = configBody.AutomationExecutionStatus.toUpperCase()
  console.log(status)

  if (status !="SUCCESS") {
        console.log("SSM Document Execution has Failed!");
        throw new Error("SSM Document Execution has Failed!");
  } 

  return configBody;
}
